describe("Event Tests",function(){var c,a,b;beforeEach(function(){c=$("#testSlider2").slider();b=document.createEvent("MouseEvents");a=false});it("'slideStart' event is triggered properly and can be binded to",function(){c.on("slideStart",function(){a=true});c.data("slider").mousedown(b);expect(a).toBeTruthy()});it("'slide' event is triggered properly and can be binded to",function(){c.on("slide",function(){a=true});c.data("slider").mousemove(b);expect(a).toBeTruthy()});it("'slide' event sets the right value on the input",function(){c.on("slide",function(){a=true;expect(isNaN(c.val())).not.toBeTruthy()});c.data("slider").mousemove(b);expect(a).toBeTruthy()});it("'slideStop' event is triggered properly and can be binded to",function(){c.on("slideStop",function(){a=true});c.data("slider").mouseup();expect(a).toBeTruthy()});it("'slideChange' event is triggered properly and can be binded to",function(){expect(true).toBeTruthy()});describe("Disabled Slider Event Tests",function(){beforeEach(function(){c.slider("disable")});it("should not trigger 'slideStart' event when disabled",function(){c.on("slideStart",function(){a=true});c.data("slider").mousedown(b);expect(a).not.toBeTruthy()});it("should not trigger 'slide' event when disabled",function(){c.on("slide",function(){a=true});c.data("slider").mousemove(b);expect(a).not.toBeTruthy()});it("should not trigger 'slideStop' event when disabled",function(){c.on("slideStop",function(){a=true});c.data("slider").mouseup();expect(a).not.toBeTruthy()});it("should not trigger 'slideChange' event when disabled",function(){expect(false).not.toBeTruthy()})});it("'slideDisabled' event is triggered properly and can be binded to",function(){c.on("slideDisabled",function(){a=true});c.slider("disable");expect(a).toBeTruthy()});it("'slideDisabled' event is triggered properly and can be binded to",function(){c.on("slideEnabled",function(){a=true});c.slider("disable");c.slider("enable");expect(a).toBeTruthy()});it("slider should not have duplicate events after calling 'refresh'",function(){a=0;c.on("slideStop",function(){a+=1});c.slider("refresh");c.data("slider").mouseup();expect(a).toEqual(1)});afterEach(function(){if(c){c.slider("destroy");c=null}})});