describe("Event Tests",function(){var f,e,d;beforeEach(function(){f=$("#testSlider2").slider();d=document.createEvent("MouseEvents");e=false});it("'slideStart' event is triggered properly and can be binded to",function(){f.on("slideStart",function(){e=true});f.data("slider").mousedown(d);expect(e).toBeTruthy()});it("'slide' event is triggered properly and can be binded to",function(){f.on("slide",function(){e=true});f.data("slider").mousemove(d);expect(e).toBeTruthy()});it("'slide' event sets the right value on the input",function(){f.on("slide",function(){e=true;expect(isNaN(f.val())).not.toBeTruthy()});f.data("slider").mousemove(d);expect(e).toBeTruthy()});it("'slideStop' event is triggered properly and can be binded to",function(){f.on("slideStop",function(){e=true});f.data("slider").mouseup();expect(e).toBeTruthy()});it("'slideChange' event is triggered properly and can be binded to",function(){expect(true).toBeTruthy()});describe("Disabled Slider Event Tests",function(){beforeEach(function(){f.slider("disable")});it("should not trigger 'slideStart' event when disabled",function(){f.on("slideStart",function(){e=true});f.data("slider").mousedown(d);expect(e).not.toBeTruthy()});it("should not trigger 'slide' event when disabled",function(){f.on("slide",function(){e=true});f.data("slider").mousemove(d);expect(e).not.toBeTruthy()});it("should not trigger 'slideStop' event when disabled",function(){f.on("slideStop",function(){e=true});f.data("slider").mouseup();expect(e).not.toBeTruthy()});it("should not trigger 'slideChange' event when disabled",function(){expect(false).not.toBeTruthy()})});it("'slideDisabled' event is triggered properly and can be binded to",function(){f.on("slideDisabled",function(){e=true});f.slider("disable");expect(e).toBeTruthy()});it("'slideDisabled' event is triggered properly and can be binded to",function(){f.on("slideEnabled",function(){e=true});f.slider("disable");f.slider("enable");expect(e).toBeTruthy()});it("slider should not have duplicate events after calling 'refresh'",function(){e=0;f.on("slideStop",function(){e+=1});f.slider("refresh");f.data("slider").mouseup();expect(e).toEqual(1)});afterEach(function(){if(f){f.slider("destroy");f=null}})});