var moment=require("../../moment");exports.daysInMonth={setUp:function(a){moment.createFromInputFallback=function(){throw new Error("input not handled by moment")};a()},"days in month":function(c){c.expect(24);var a=[31,29,31,30,31,30,31,31,30,31,30,31],b;for(b=0;b<12;b++){c.equal(moment([2012,b]).daysInMonth(),a[b],moment([2012,b]).format("L")+" should have "+a[b]+" days. (beginning of month "+b+")")}for(b=0;b<12;b++){c.equal(moment([2012,b,a[b]]).daysInMonth(),a[b],moment([2012,b,a[b]]).format("L")+" should have "+a[b]+" days. (end of month "+b+")")}c.done()},"days in month leap years":function(a){a.expect(4);a.equal(moment([2010,1]).daysInMonth(),28,"Feb 2010 should have 28 days");a.equal(moment([2100,1]).daysInMonth(),28,"Feb 2100 should have 28 days");a.equal(moment([2008,1]).daysInMonth(),29,"Feb 2008 should have 29 days");a.equal(moment([2000,1]).daysInMonth(),29,"Feb 2000 should have 29 days");a.done()}};