var moment=require("../../moment");exports.daysInMonth={setUp:function(b){moment.createFromInputFallback=function(){throw new Error("input not handled by moment")};b()},"days in month":function(f){f.expect(24);var e=[31,29,31,30,31,30,31,31,30,31,30,31],d;for(d=0;d<12;d++){f.equal(moment([2012,d]).daysInMonth(),e[d],moment([2012,d]).format("L")+" should have "+e[d]+" days. (beginning of month "+d+")")}for(d=0;d<12;d++){f.equal(moment([2012,d,e[d]]).daysInMonth(),e[d],moment([2012,d,e[d]]).format("L")+" should have "+e[d]+" days. (end of month "+d+")")}f.done()},"days in month leap years":function(b){b.expect(4);b.equal(moment([2010,1]).daysInMonth(),28,"Feb 2010 should have 28 days");b.equal(moment([2100,1]).daysInMonth(),28,"Feb 2100 should have 28 days");b.equal(moment([2008,1]).daysInMonth(),29,"Feb 2008 should have 29 days");b.equal(moment([2000,1]).daysInMonth(),29,"Feb 2000 should have 29 days");b.done()}};