describe("DOM Events",function(){describe('"change"',function(){it("should be triggered once by addItem()",function(b){var c=setup_test("<select>",{valueField:"value",labelField:"value",options:[{value:"a"},{value:"b"},],items:["a"]});var a=0;c.$select.on("change",function(){a++});c.selectize.addItem("b");window.setTimeout(function(){expect(a).to.be.equal(1);b()},0)});it("should be triggered once by removeItem()",function(b){var c=setup_test("<select multiple>",{valueField:"value",labelField:"value",options:[{value:"a"},{value:"b"},],items:["a","b"]});var a=0;c.$select.on("change",function(){a++});c.selectize.removeItem("b");window.setTimeout(function(){expect(a).to.be.equal(1);b()},0)});it("should be triggered once by clear()",function(b){var c=setup_test("<select multiple>",{valueField:"value",labelField:"value",options:[{value:"a"},{value:"b"},],items:["a","b"]});var a=0;c.$select.on("change",function(){a++});c.selectize.clear();window.setTimeout(function(){expect(a).to.be.equal(1);b()},0)})})});